// Access the actual JSON data inside the item wrapper
const item = $input.item.json;

// Now access the serp object inside that JSON data
const serp = item.serp || {};

return {
  notionPageId: item.notionPageId,
  title: item.title,
  keyword: item.keyword,

  // Safely extract the arrays
  organic_top5: (serp.organic_results || []).slice(0, 5),
  questions_top3: (serp.related_questions || []).slice(0, 3),

  total_results: serp.search_information?.total_results ?? null
};

// Access the incoming item
const item = $input.item.json;

// 1. Get the text directly
const aiText = item.text || item.output || ""; 

// 2. SMART EXTRACTION: Find the JSON object inside the text
// We look for the first "{" and the last "}" and ignore everything else.
let cleanJson = "{}";
const firstOpen = aiText.indexOf('{');
const lastClose = aiText.lastIndexOf('}');

if (firstOpen !== -1 && lastClose !== -1) {
  cleanJson = aiText.substring(firstOpen, lastClose + 1);
}

// 3. Parse the text into a real Object
let parsedData = {};
try {
  parsedData = JSON.parse(cleanJson);
} catch (error) {
  // Fallback if parsing fails
  parsedData = { article_type: "general", intent: "unknown", reasoning: "Parsing error" };
}

return {
  // Keep the original data
  ...item,

  // Add the new AI analysis fields
  intent: parsedData.intent,

  // FORCE LOWERCASE for the Switch node
  article_type: (parsedData.article_type || "").toLowerCase(), 

  reasoning: parsedData.reasoning
};

// 1. Get the text safely
const longText = $('Format Google').first().json.text || "";

// 2. Split by new lines
const lines = longText.split('\n');
const result = [];

for (const line of lines) {
  let cleanLine = line.trim();

  if (cleanLine.length > 0) {
    let blockType = "paragraph";
    let content = cleanLine;

    // RULE 1: Standard Headings
    if (/^[\*]*#\s/.test(cleanLine) || /^#\s/.test(cleanLine)) {
       blockType = "heading_1";
       content = cleanLine.replace(/^[\*]*#\s+/, "").replace(/[\*]*$/, ""); 
    }
    else if (/^[\*]*##\s?/.test(cleanLine)) {
       blockType = "heading_2";
       content = cleanLine.replace(/^[\*]*##\s*/, "").replace(/[\*]*$/, ""); 
    }
    else if (/^[\*]*###\s?/.test(cleanLine)) {
       blockType = "heading_3";
       content = cleanLine.replace(/^[\*]*###\s*/, "").replace(/[\*]*$/, ""); 
    }

    // RULE 1.5: "Step X" & "Conclusion" -> Force as Heading 2
    else if (/^Step\s\d+/i.test(cleanLine)) {
       blockType = "heading_2";
    }
    else if (/^(Conclusion|Summary|Final Thoughts):?$/i.test(cleanLine)) {
       blockType = "heading_2";
    }

    // RULE 2: Bullet Points & Numbers
    else if (/^\d+\.\s/.test(cleanLine)) {
       blockType = "numbered_list_item";
       content = cleanLine.replace(/^\d+\.\s/, ""); 
    }
    else if (/^[\*\-â€¢]\s/.test(cleanLine)) {
      blockType = "bulleted_list_item";
      content = cleanLine.replace(/^[\*\-â€¢]\s+/, ""); 
    }

    // RULE 3: Screenshots
    else if (cleanLine.startsWith("[SCREENSHOT")) {
      blockType = "to_do"; 
      content = cleanLine.replace("[SCREENSHOT:", "ðŸ“¸ Capture:").replace("]", "");
    }

    // --- THE FIX: CLEAN UP BOLD STARS ---
    // If it's a normal paragraph, remove the ** symbols so it looks clean
    if (blockType === "paragraph" || blockType.includes("list_item")) {
        content = content.replace(/\*\*/g, ""); // Removes **
        content = content.replace(/\*/g, "");   // Removes single *
    }

    // Output
    result.push({ 
      json: { text_chunk: content, block_type: blockType } 
    });
  }
}

return result;

